
public class 연산자80페이지ArithOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 7;
		int num2 = 3;
		
			System.out.println("num1 + num2 =" + (num1 + num2));
			System.out.println("num1 - num2 =" + (num1 - num2));
			System.out.println("num1 * num2 =" + (num1 * num2));
			System.out.println("num1 / num2 ="+ (num1 / num2));
			System.out.println("num1 % num2 =" + (num1 % num2));
			
	}
}
