package sec03.sec02.exam10;

public class 대입연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result = 0;
		result +=10;
		System.out.println("result="+ result);
		
		result -= 5;
		System.out.println("result="+ result);
		
		result *= 3;
		System.out.println("result="+ result);
		
		result /= 5;
		System.out.println("result="+ result);
		
		result %= 3;
		System.out.println("result="+ result);
	}

}
